		//פונקציה אינטרוולית הפועלת פעם בשניה, מגדילה את המונה של השניות במשחק ב-1 ולאחר מכן מציבה את התוצאה החדשה בתיבת זמן
		function clockMoving() {
			s++; //קפיצה בשנייה ותנאים למעבר דקות
			if (s > 59) {
				s = 0;
				m++;
			}
			if (m > 59) {
				m = 0;
			}
		
			//תנאים להצבה מתאימה של תיבת הטקסט לשעון
			if (m < 10 && s < 10) {
				stage.getChildByName("edge2").time.getChildByName("clockTxt").text = "0" + m + ":0" + s;
			} else if (m < 10 && s >= 10) {
				stage.getChildByName("edge2").time.getChildByName("clockTxt").text = "0" + m + ":" + s;
			} else if (m >= 10 && s < 10) {
				stage.getChildByName("edge2").time.getChildByName("clockTxt").text = m + ":0" + s;
			} else {
				stage.getChildByName("edge2").time.getChildByName("clockTxt").text = m + ":" + s;
			}
		}
		
		
